import { useState } from "react";
import DropDownComponent from "../../common/dropdown/dropDown";

function PlayerStatsController({ items }) {

  const [selectedPlayerID, setSelectedPlayerID] = useState(items[0].PlayerID);

  const handleFieldChange = playerID => {
    setSelectedPlayerID({ playerID });
    console.log("call back..");
  };


  return (
    <div>
      <DropDownComponent items={items} onChange={handleFieldChange}/>
    </div>
  );
}

export default PlayerStatsController;
