import { Fragment, useState } from "react";
import { Listbox, Transition } from "@headlessui/react";
import { CheckIcon, ChevronUpDownIcon } from "@heroicons/react/20/solid";

function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

function PlayerStatsController({ items }) {
  const [selected, setSelected] = useState(items[0]);

  const handleSelect = (e) => {

    console.log(e.currentTarget);

  }

  return (
    <Listbox value={selected} by="PlayerID" onChange={setSelected}>
      <Listbox.Button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" >
        <span className="flex items-center">
          <span className="ml-3 block truncate">
            {selected.Rank + ". " + selected.Name}
          </span>
          <ChevronUpDownIcon
            className="h-5 w-5 text-gray-400"
            aria-hidden="true"
          />
        </span>
      </Listbox.Button>

      <Listbox.Options>
        {items.map((item) => (
          <Listbox.Option key={item.PlayerID} value={item} as={Fragment} onClick={handleSelect} data-Name="{item.Name}">
            {({ active, selected }) => (
              <li
                className={`${
                  active ? "bg-blue-500 text-white" : "bg-white text-black"
                }`}
              >
                {item.Name}
              </li>
            )}
          </Listbox.Option>
        ))}
      </Listbox.Options>
    </Listbox>
  );
}

export default PlayerStatsController;
